import {Component, Input, OnInit} from '@angular/core';
import {Product, products} from "../products";
import {ActivatedRoute} from "@angular/router";

@Component({
    selector: 'app-product-item',
    templateUrl: './product-item.component.html',
    styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent implements OnInit{
    @Input() product !: Product


    constructor(private route: ActivatedRoute,) {

    }

    ngOnInit(): void {

    }

    share(link: string) {
        window.alert('The product has been shared!');
        window.open('https://wa.me/7073057588');
    }

    onNotify() {
        window.alert("You will be notified when the product goes on sale");
    }

    like() {
        products[this.product.id-1].like++;
    }


}
<div class="">
    <h3>
        <a [title]="product.name + ' details'" [routerLink]="['/products', product.id]">
            {{ product.name }}
        </a>
    </h3>
    <img src="{{ product.image }}" alt="img" class=".img">

    <p *ngIf="product.description">
        Description: {{ product.description }}
    </p>

    <button type="button" (click)="share(product.link)">
        Share
    </button>

    <app-product-alerts
            [product]="product"
            (notify)="onNotify()">
    </app-product-alerts>


        <div>

            <img src="https://cdn.worldvectorlogo.com/logos/facebook-reaction-like.svg"
                 alt="img"
                 style="width: 20px"
                 (click)="like()"
            >

            <p> {{product.like}} </p>
        </div>

</div>
